rt("<?php echo Session::instance()->get_once("flash",'default'); ?>", "");<?php } ?><?php if (Session::instance()->get("register_info") != '') {?> 	jAlert("<?php echo Session::instance()->get_once("register_info",'default'); ?>", ""); <?php } ?><?php if (Session::instance()->get("auth_error") != '') {?> 	jAlert("<?php echo Session::instance()->get_once("auth_error",'default'); ?>", "");<?php } ?>$('input[type="submit"]').click(function(){$(this).parents('form').submit(); return false; });         $('#contactForm').submit(function(e){	e.preventDefault();	jProcess('Please do not close your browser ...','Submitting');	$.ajax({		url     : base_URL + 'xhr/contact/',		type    : 'POST',		data    : $(this).serialize(),		success : function(ms)		{			var me = $.parseJSON(ms);						if (typeof me.errors !== 'undefined') {								var tmp = '';								$.each(me.errors, function( i, m ) {						tmp += m; 				});								jAlert(tmp,'Errors');							} else if(me.result === 'sent') {								$('#contactForm').find('input[type="text"],textarea').each(function (){					$(this).val('');				});								jAlert('Sent, Thank you for your message, we will contact you as soon as possible','Sent');							}					}	});	return false;                                });$('.reload_captcha').click(function() {	var url	= $(this).attr('href');			$.ajax({		type: "POST",		url: url,		data: 'file=true',		cache: false,		async: false,			success: function(msg){			$('.reload_captcha').empty().html(msg);			// Need random for browser recache			img		= $('.reload_captcha').find('.captcha');			src		= img.attr('src');			ran		= img.fadeOut(10).fadeIn(220).attr('src', src + '?=' + Math.random());		},		complete: function(msg) {},		error: function(msg) {}	});	return false;	});var owl = $("#owl-demo");	owl.owlCarousel({	autoHeight : true,	pagination : false,	navigation: true,	lazyLoad : true,	transitionStyle:"fade",	items : 4, //10 items above 1000px browser